// Sound Base64
const beep = new Audio("data:audio/wav;base64,");

window.addEventListener('keydown', function(e){
    if (e.shiftKey) {
        if (e.keyCode === 112){
            // alert('Shift+F1');
            CountAdd('F1');
            beep.play();
        }
        if (e.keyCode === 113){
            // alert('Shift+F2');
            CountAdd('F2');
            beep.play();
        }
        if (e.keyCode === 114){
            // alert('Shift+F3');
            CountAdd('F3');
            beep.play();
        }
        if (e.keyCode === 115){
            // alert('Shift+F4');
            CountAdd('F4');
            beep.play();
        }
        if (e.keyCode === 116){
            // alert('Shift+F5');
            CountAdd('F5');
            beep.play();
        }
        if (e.keyCode === 117){
            // alert('Shift+F6');
            CountAdd('F6');
            beep.play();
        }
        if (e.keyCode === 118){
            // alert('Shift+F7');
            CountAdd('F7');
            beep.play();
        }
        if (e.keyCode === 119){
            // alert('Shift+F8');
            CountAdd('F8');
            beep.play();
        }
        if (e.keyCode === 120){
            // alert('Shift+F9');
            CountAdd('F9');
            beep.play();
        }
    }
});

/**
 * StorageStore Count Add
 */
const CountAdd = (keyLabel) => {
    const key = `${keyLabel}Count`;
    chrome.storage.local.get(key, (res) => {
        if (res[key] !== undefined) {
            res[key]++;
        } else {
            res[key] = 1;
        }
        chrome.storage.local.set(res);
    });
};
